import React, { Component } from 'react'

class fontSize extends Component {
    state={
        Fontsize:null
    }

    // handleChange=(e)=>{
    //     this.setState({
    //         ...this.state,
    //         textFont:e.target.value
    //     });
    //   }
    handleChange=(e)=>{
        var lang =e.target.value;
        this.props.handleFontSize(lang)
    }

    render(){
        console.log(this.state.textFont)

        return (
            <div className="mt-2 ml-2">
                <select id="input-fontsize" value={this.state.selected}  onChange={this.handleChange}>
                    <option value="5">10px</option>      
                    <option value="10">14px</option>
                    <option value="20">17px</option>
                    <option value="40">20px</option>
                    <option value="50">24px</option>
                    <option value="60">28px</option>
                    <option value="70">30px</option>
                    <option value="80">32px</option>
                    <option value="90">35px</option>
                    <option value="200px">40px</option>
                </select>


            </div>
        )
    }
}
export default fontSize
