import React, { Component } from 'react'
import FeatureImg from '../feature-img/FeatureImg'
import Text from '../text/text'
import Textsecond from '../text/textsecond'
import TextThird from '../text/textThird'
import Maindash from '../text-feature/maindash'

class Editor extends Component {
    state={
        img:null,
        textFirst:null,
        textsecond:null,
        textThird:null,
        textAlign:null,
        fontsize:null
    }
    selectedimg=(img)=>{
        this.setState({
            ...this.state,
            img:img
        })
    }
    FirstTextItem=(e)=>{
        this.setState({
            ...this.state,
            textFirst:e.target.value
        })

    }

    secondTextItem=(e)=>{
        this.setState({
            ...this.state,
            textsecond:e.target.value
        })

    }
    thirdTextItem=(e)=>{
        this.setState({
            ...this.state,
            textThird:e.target.value
        })

    }

    handletextFeaturetext=(text)=>{
        this.setState({
            ...this.state,
            textAlign:text
        })

    }
    handletextFeatureFontSize=(font)=>{
        this.setState({
            ...this.state,
            fontsize:font
        })

    }
    handletextFeatureVertical=(vertical)=>{
        this.setState({
            ...this.state,
            verticalAlign:vertical
        })

    }
    handletextFeatureFont=(font)=>{
        this.setState({
            ...this.state,
            font:font
        })

    }
    handletextFeatureColor=(color)=>{
        this.setState({
            ...this.state,
            color:color
        })

    }
    render() {
        console.log(this.state)

        return (
            <div className="container mt-5">
                <div className="row">
                    <div className="col-md-5">
                        <div className="card" style={{height:450,width:450}}>
                            <img src={this.state.img}/>
                            <div className="firsttext" style={{marginTop:-400}}>
                                <h3>{this.state.textFirst}</h3>
                            </div>
                            <div className="secondtext">
                                <h3>{this.state.textsecond}</h3>
                            </div>
                            <div className="thirdtext">
                                <h3>{this.state.textThird}</h3>
                            </div>  
                        </div>


                    </div>
                    <div className="col-md-7">
                        <div className="row">
                            <div className="col-md-7" id="editor1">
                                <div className="featureimg">
                                <FeatureImg selectedimg={this.selectedimg}/>
                                </div>
                                <div className="edittext">
                                    <Text FirstTextItem={this.FirstTextItem}/>
                                </div>
                                <div className="edittext">
                                    <Textsecond secondTextItem={this.secondTextItem} />
                                </div>
                                <div className="edittext">
                                    <TextThird thirdTextItem={this.thirdTextItem} />
                                </div>
                            </div>
                            <div className="col-md-5">
                                <Maindash handletextFeaturetext={this.handletextFeaturetext} 
                                    handletextFeatureFontSize={this.handletextFeatureFontSize}
                                    handletextFeatureVertical={this.handletextFeatureVertical}
                                    handletextFeatureFont={this.handletextFeatureFont}
                                    handletextFeatureColor={this.handletextFeatureColor}
                                />
                            </div>
                        </div>

                    </div>
                </div>


            </div>
        )
    }
}
export default Editor
