import React, { Component } from 'react'
import FeatureImg from '../feature-img/FeatureImg'
import Text from '../text/text'

class Editor extends Component {
    state={
        img:null,
        textFirst:null
    }
    selectedimg=(img)=>{
        this.setState({
            ...this.state,
            img:img
        })
    }
    FirstTextItem=(e)=>{
      this.setState({
          ...this.state,
          textFirst:e.target.value
      })

    }
    SecondTextItem=(e)=>{
        this.setState({
            ...this.state,
            textFirst:e.target.value
        })

      }
    ThirdTextItem=(e)=>{
        this.setState({
            ...this.state,
            textFirst:e.target.value
        })

      }
    render() {

        return (
            <div className="container mt-5">
                <div className="row">
                    <div className="col-md-6" style={{position:'relative'}}>
                        <div className="card" style={{height:500,width:500}}>
                            <img src={this.state.img}/>
                            <div className="firsttext" style={{marginTop:-400}}>
                                <h3>{this.state.textFirst}</h3>
                            </div>

                        </div>


                    </div>
                    <div className="col-md-6">
                        <div className="featureimg">
                            <FeatureImg selectedimg={this.selectedimg}/>
                        </div>
                        <div className="edittext">
                            <Text FirstTextItem={this.FirstTextItem} SecondTextItem={SecondTextItem} ThirdTextItem={ThirdTextItem} />

                        </div>
                    </div>
                </div>


            </div>
        )
    }
}
export default Editor
