import React, { Component } from 'react'

class checktext extends Component {
    state={
        ischeck:false,
    };
    handleBold = ()=>{
        this.setState((prevState)=>{
            return {ischeck: !prevState.ischeck};

        });
    };
    render() {
        console.log(this.state.ischeck)
        return (
            <div>
            <label onClick={ this.handleBold }>Bold</label>
            <input type="checkbox" onChange={ this.handleBold }></input>

            </div>
        )
    }
}
export default checktext
