import React, { Component } from 'react'
import html2canvas from 'html2canvas'

class screenCapture extends Component {
    render() {
        function handlepassurl(url){
            this.props.handleshareurl(url)
        }

        const test = () => {
            html2canvas(document.querySelector("#capture"))
               .then(function (canvas) {
                  document.body.appendChild(canvas);
                  const base64URL = canvas.toDataURL('image/jpeg').replace('image/jpeg', 'image/octet-stream');
                  handlepassurl(base64URL)

               });
          }
        return (
            <div>
                <button className="btn btn-primary" onClick={test}>click</button>
            </div>
        )
    }
}
export default screenCapture
