import React, { Component } from 'react'
import FeatureImg from '../feature-img/FeatureImg'
import Text from '../text/text'
import Textsecond from '../text/textsecond'
import TextThird from '../text/textThird'
import MaindashTextOne from '../text-feature-1/maindashTextOne'
import MaindashTextTwo from '../text-feature-2/maindashTextTwo'
import SocialShare from '../editor/socialShare'
import html2canvas from 'html2canvas'
import ScreenCapture from './screenCapture'


import {
    FacebookShareButton,
    FacebookIcon
  } from 'react-share';


class Editor extends Component {
    state={
        x:0,
        y:0,
        xs:20,
        ys:20,
        url:null,
        shareurl:null,
        textFirst:null,
        dtextO:false,
        dtextT:false,
        textsecond:null,
        textThird:null,
        isbold:null,
        isitalic:null,
        textAlign:null,
        fontsize:null,
        verticalAlign:null,
        font:null,
        color:null,
        isboldT:null,
        isitalicT:null,
        textAlignT:null,
        fontsizeT:null,
        verticalAlignT:null,
        fontT:null,
        colorT:null,
        colorOT:null,
        isTopDragging: false,
        isBottomDragging: false,
        topY: "10%",
        topX: "50%",
        bottomX: "50%",
        bottomY: "90%"

    }
    // Select background image.......

    selectedimg=(url)=>{
        this.setState({
            ...this.state,
            url:url
        })
    }
    // Select Text input......

    FirstTextItem=(e)=>{
        this.setState({
            ...this.state,
            textFirst:e.target.value
        })

    }

    secondTextItem=(e)=>{
        this.setState({
            ...this.state,
            textsecond:e.target.value
        })

    }
    thirdTextItem=(e)=>{
        this.setState({
            ...this.state,
            textThird:e.target.value
        })

    }
    // display property of maindashText

    textdisplay=(e)=>{
        this.setState((prevState)=>{
            return {dtextO: !prevState.dtextO};    
        });
    }
    textSdisplay=(e)=>{
        this.setState((prevState)=>{
            return {dtextT: !prevState.dtextT};    
        });
    }
    // MaindashTextOne feature.......

    handletextFeaturetext=(text)=>{
        this.setState({
            ...this.state,
            textAlign:text
        })

    }
    handletextFeatureFontSize=(font)=>{
        this.setState({
            ...this.state,
            fontsize:font
        })

    }
    handletextFeatureVertical=(vertical)=>{
        this.setState({
            ...this.state,
            verticalAlign:vertical
        })

    }
    handletextFeatureFont=(font)=>{
        this.setState({
            ...this.state,
            font:font
        })

    }
    handletextFeatureColor=(color)=>{
        this.setState({
            ...this.state,
            color:color
        })

    }
    handleChecklistItalic=(e)=>{
        this.setState({
            ...this.state,
            isitalic:e
        })
    }
    handleChecklistBold=(e)=>{
        this.setState({
            ...this.state,
            isbold:e
        })
    }
    // MaindashTextTwo features........


    handletextFeaturetextTwo=(text)=>{
        this.setState({
            ...this.state,
            textAlignT:text
        })

    }
    handletextFeatureFontSizeTwo=(font)=>{
        this.setState({
            ...this.state,
            fontsizeT:font
        })

    }
    handletextFeatureVerticalTwo=(vertical)=>{
        this.setState({
            ...this.state,
            verticalAlignT:vertical
        })

    }
    handletextFeatureFontTwo=(font)=>{
        this.setState({
            ...this.state,
            fontT:font
        })

    }
    handletextFeatureColorTwo=(color)=>{
        this.setState({
            ...this.state,
            colorT:color
        })

    }
    handletextFeatureOutColorTwo=(color)=>{
        this.setState({
            ...this.state,
            colorOT:color
        })
    }
    handleChecklistItalicTwo=(e)=>{
        this.setState({
            ...this.state,
            isitalicT:e
        })
    }
    handleChecklistBoldTwo=(e)=>{
        this.setState({
            ...this.state,
            isboldT:e
        })
    }
    getStateObj = (e, type) => {

        const xOffset = e.clientX ;
        const yOffset = e.clientY ;
        let stateObj = {};
        if (type === "bottom") {
          stateObj = {
            isBottomDragging: true,
            isTopDragging: false,
            bottomX: `${xOffset}px`,
            bottomY: `${yOffset}px`
          }
        } else if (type === "top") {
          stateObj = {
            isTopDragging: true,
            isBottomDragging: false,
            topX: `${xOffset}px`,
            topY: `${yOffset}px`
          }
        }
        return stateObj;
      }

      handleMouseDown = (e, type) => {
        const stateObj = this.getStateObj(e, type);
        document.addEventListener('mousemove', (event) => this.handleMouseMove(event, type));
        this.setState({
          ...stateObj
        })
      }

      handleMouseMove = (e, type) => {
        if (this.state.isTopDragging || this.state.isBottomDragging) {
          let stateObj = {};
          if (type === "bottom" && this.state.isBottomDragging) {
            stateObj = this.getStateObj(e, type);
          } else if (type === "top" && this.state.isTopDragging){
            stateObj = this.getStateObj(e, type);
          }
          this.setState({
            ...stateObj
          });
        }
      };

      handleMouseUp = (event) => {
        document.removeEventListener('mousemove', this.handleMouseMove);
        this.setState({
          isTopDragging: false,
          isBottomDragging: false
        });
      }

    handleshareurl=(url)=>{
        this.setState({
            ...this.state,
            shareurl:url
        })
    }

    render() {

       console.log(this.state.shareurl)

        return (
            <div className="container mt-5">
                <div className="row mb-5" style={{backgroundColor:'#fff',height:'100vh'}}>

                    <div className="col-md-5 mt-5" onClick={this.handleMouseEnter}  >

                        <div className="card" id='capture' style={{position:"relative",border:'none'}} >
                            <img src={this.state.url} style={{width:450}}/>

                            <text className="text1"     
                                style={{
                                    position:"absolute",
                                    textAlign:this.state.textAlign,
                                    marginTop:20,
                                    color:this.state.color,
                                    fontFamily:this.state.font,
                                    fontSize:this.state.fontsize,
                                    fontStyle:this.state.isitalic ? "italic":"normal",
                                    fontWeight:this.state.isbold ? "bold":"normal",
                                    }}
                                      x={this.state.topX}
                                    y={this.state.topY}
                                    dominantBaseline="middle"
                                    textAnchor="middle"
                                    onMouseDown={event => this.handleMouseDown(event, 'top')}
                                    onMouseUp={event => this.handleMouseUp(event, 'top')}
                                    >
                                      {this.state.textFirst}    
                            </text>
                            <text className="text2" 
                            style={{ 
                                    position:"absolute",
                                    textAlign:this.state.textAlignT,
                                    color:this.state.colorT,
                                    fontFamily:this.state.fontT,
                                    fontSize:this.state.fontsizeT,
                                    fontStyle:this.state.isitalicT ? "italic":"normal",
                                    fontWeight:this.state.isboldT ? "bold":"normal",
                                    color:this.state.colorT,
                                    webkitTextFillColor:this.state.colorOT, 
                                    webkitTextStrokeWidth: '1px',
                                    webkitTextStrokeColor: this.state.colorT,                                
                                    }}
                                        dominantBaseline="middle"
                                        textAnchor="middle"
                                        x={this.state.bottomX}
                                        y={this.state.bottomY}
                                        onMouseDown={event => this.handleMouseDown(event, 'bottom')}
                                        onMouseUp={event => this.handleMouseUp(event, 'bottom')}
                                        >
                                    {this.state.textsecond}   
                            </text>
                            <div className="thirdtext">
                                <h3>{this.state.textThird}</h3>
                            </div>  
                        </div>



                    </div>
                    <div className="col-md-7 mt-5">
                        <div className="row">
                            <div className="col-md-7" id="editor1">
                                <div className="featureimg">
                                    <div className="card" style={{border:'none'}}>
                                        <div style={{ height: 300, overflowY: "scroll" }}>
                                            <FeatureImg selectedimg={this.selectedimg} />
                                        </div>
                                    </div>
                                </div>
                                <div className="edittext">
                                    <Text FirstTextItem={this.FirstTextItem} textdisplay={this.textdisplay}/>
                                </div>
                                <div className="edittext">
                                    <Textsecond secondTextItem={this.secondTextItem} textSdisplay={this.textSdisplay} />
                                </div>
                                <div className="edittext">
                                    <TextThird thirdTextItem={this.thirdTextItem} />
                                </div>
                                <FacebookShareButton url={this.state.shareurl}>
                                    <FacebookIcon logoFillColor="white" />
                                </FacebookShareButton>
                                <ScreenCapture handleshareurl={this.handleshareurl} />


                            </div>
                            {this.state.dtextO ? 
                                <div className="col-md-5">
                                <MaindashTextOne 
                                    handletextFeaturetext={this.handletextFeaturetext} 
                                    handletextFeatureFontSize={this.handletextFeatureFontSize}
                                    handletextFeatureVertical={this.handletextFeatureVertical}
                                    handletextFeatureFont={this.handletextFeatureFont}
