import React, { Component } from 'react'

class textAlign extends Component {
    state={
        right:false,
        left:false,
        center:true
    }
    handlealignLeft=(e)=>{
        this.state({
            ...this.state,
            right:false,
            left:true,
            center:false
        })
    }
    handlealignRight=(e)=>{
        this.state({
            ...this.state,
            right:true,
            left:false,
            center:false
        })
    }
    handlealignCenter=(e)=>{
        this.state({
            ...this.state,
            right:false,
            left:false,
            center:false
        })
    }

    render() {

        return (

            <div>
                <div class="text-align mt-3 ml-2">
                    <div  class="avatar1" style={{color:'#fff',backgroundColor:'#000'}} onclick={this.handlealignLeft} >Left</div>
                    <div  class="avatar1" style={{color:'#000',backgroundColor:'#ddd'}} onclick={this.handlealignCenter}>Center</div>
                    <div  class="avatar1" style={{color:'#000',backgroundColor:'#ddd'}} onclick={this.handlealignRight}>Right</div>

                </div>
            </div>
        )

    }
}
export default textAlign
