import React, { Component } from 'react'

class checktext extends Component {
    state={
        isbold:true,
        isitalic:true
    };
    handleBold = ()=>{
        this.setState((prevState)=>{
            return {isbold: !prevState.isbold};    
        });
        this.props.handleChecklist(this.state)
    };
    handleItalic = ()=>{
        this.setState((prevState)=>{
            return {isitalic: !prevState.isitalic};    
        });
        this.props.handleChecklist(this.state)
    };
    render() {

        return (
            <div>
                <div>
                <input type="checkbox" onChange={ this.handleBold }></input>
                <label style={{marginLeft:10}}><b>Bold</b></label>
            </div>
           <div >
                <input type="checkbox" onChange={ this.handleItalic }></input>
                <label style={{marginLeft:10}} ><i>Italic</i></label>
           </div>


            </div>
        )
    }
}
export default checktext
