import React, { Component } from 'react'

class fontSize extends Component {
    state={
        Fontsize:null
    }

    // handleChange=(e)=>{
    //     this.setState({
    //         ...this.state,
    //         textFont:e.target.value
    //     });
    //   }
    handleChange=(e)=>{
        var lang =e.target.value;
        this.props.handleFontSize(lang)
    }

    render(){
        console.log(this.state.textFont)

        return (
            <div className="mt-2 ml-2">
                <select id="input-fontsize" value={this.state.selected}  onChange={this.handleChange}>
                    <option value="5px">10px</option>      
                    <option value="8px">14px</option>
                    <option value="10px">17px</option>
                    <option value="12px">20px</option>
                    <option value="15px">24px</option>
                    <option value="17px">28px</option>
                    <option value="20px">30px</option>
                    <option value="23px">32px</option>
                    <option value="23px">35px</option>
                    <option value="30px">40px</option>
                </select>


            </div>
        )
    }
}
export default fontSize
